CREATE DATABASE IF NOT EXISTS artbox CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

USE artbox;

CREATE TABLE IF NOT EXISTS `artbox`.`oeuvres` 
(
    `id` INT NOT NULL AUTO_INCREMENT COMMENT 'Identifiant unique de l\'oeuvre' , 
    `titre` VARCHAR(256) NOT NULL COMMENT 'Titre de l\'oeuvre' , 
    `artiste` VARCHAR(128) NOT NULL COMMENT 'Nom de l\'artiste' , 
    `description` TEXT NOT NULL COMMENT 'Description de l\'oeuvre' , 
    `image` VARCHAR(256) NOT NULL COMMENT 'Chemin local vers l\'image de l\'oeuvre' , 
    PRIMARY KEY (`id`)
) 
ENGINE = InnoDB COMMENT = 'Liste des oeuvres présentées sur artbox';

